// Reusable Collapsible Section Component
const CollapsibleSection = ({ title, children, defaultCollapsed = false }) => {
  const [isCollapsed, setIsCollapsed] = useState(defaultCollapsed);
  const toggle = () => setIsCollapsed(!isCollapsed);

  return (
    <div style={styles.section}>
      <div style={styles.sectionHeader} onClick={toggle}>
        <span>{title}</span>
        <span style={{ transform: isCollapsed ? 'rotate(-90deg)' : 'rotate(0)', transition: 'transform 0.3s ease' }}>â–¼</span>
      </div>
      <div style={{...styles.sectionContent, ...(isCollapsed ? styles.collapsedContent : {})}}>
        {children}
      </div>
    </div>
  );
};

